import { render } from '@testing-library/react'
import Nav from '../nav/nav'
import React from 'react'
import Context from '../Context'

class Cart extends React.Component {
  render() {
    const { cartList } = this.context
    return (
      <div>
        <Nav />
        <div>
          <div>
            <h2>Cart</h2>
          </div>
          <ul>
            {cartList.map((item, index) => {
              return (
                <li key={item.id}>
                  <h2>{item.brand}</h2>
                  <h2>{item.name}</h2>
                  <h2>
                    {item.currency}
                    {item.amount}
                  </h2>
                  <img src={item.gallery[index]} />
                  <h2>{item.quantity}</h2>
                </li>
              )
            })}
          </ul>
        </div>
      </div>
    )
  }
}

Cart.contextType = Context

export default Cart
