import Nav from '../nav/nav'
import React from 'react'
import Styles from './cart.module.css'
import Context from '../Context'
import CartComponent from './cartComponents/CartComponent'

class Cart extends React.Component {
  constructor() {
    super()
    this.state = {
      index: 0,
      data: undefined,
    }
  }

  render() {
    const { cartList } = this.context
    const { removeFromCart } = this.context
    const { addToCart } = this.context
    const { index } = this.context

    return (
      <div className={Styles['main-container']}>
        <div>
          <h2 className={Styles['cart-text']}>Cart</h2>
        </div>
        <ul>
          {cartList.map((item) => {
            return (
              <CartComponent
                itemId={item.id}
                itemBrand={item.brand}
                itemName={item.name}
                itemCurrency={item.prices[index].currency.symbol}
                itemAmount={item.prices[index].amount}
                itemAttributes={item.attributes}
                itemQuantity={item.quantity}
              />
            )
          })}
        </ul>
      </div>
    )
  }
}

Cart.contextType = Context

export default Cart
