import React from 'react'
import Styles from './cartComponent.module.css'

class CartComponent extends React.Component {
  render() {
    return (
      <li key={this.props.itemId} className={Styles['description-container']}>
        <div>
          <h2 className={Styles.brand}>{this.props.itemBrand}</h2>
          <h2 className={Styles.name}>{this.props.itemName}</h2>
          <h2 className={Styles.price}>
            {this.props.itemCurrency}
            {this.props.itemAmount}
          </h2>
          {Object.entries(this.props.itemAttributes).map(([key, value]) => {
            return (
              <div key={key}>
                <h2>{key}</h2>
                <h2>{value}</h2>
              </div>
            )
          })}
        </div>
        <div className={Styles['right-side']}>
          <div className={Styles['quantity-container']}>
            <button
              className={Styles.btn}
              onClick={() => addToCart(this.props.addToCartItem)}
            >
              +
            </button>
            <h2 className={Styles.quantity}>{item.quantity}</h2>
            <button
              onClick={() => removeFromCart(this.props.removeFromCartItem)}
              className={Styles.btn}
            >
              -
            </button>
          </div>
          <img src={this.props.itemImg} className={Styles.img} />
        </div>
      </li>
    )
  }
}

export default CartComponent
