import Nav from '../nav/nav'
import React from 'react'
import Styles from './cart.module.css'
import Context from '../Context'
import LeftArrow from './img/LeftArrow.svg'
import RightArrow from './img/RightArrow.svg'

class Cart extends React.Component {
  constructor() {
    super()
    this.state = {
      index: 0,
    }
  }
  render() {
    const { cartList } = this.context
    const { removeFromCart } = this.context
    const length = cartList.item.length
    return (
      <div>
        <Nav />
        <div className={Styles['main-container']}>
          <div>
            <h2 className={Styles['cart-text']}>Cart</h2>
          </div>
          <ul>
            {cartList.map((item) => {
              return (
                <li key={item.id} className={Styles['description-container']}>
                  <div>
                    <h2 className={Styles.brand}>{item.brand}</h2>
                    <h2 className={Styles.name}>{item.name}</h2>
                    <h2 className={Styles.price}>
                      {item.currency}
                      {item.amount}
                    </h2>
                  </div>
                  <div className={Styles['right-side']}>
                    <div className={Styles['quantity-container']}>
                      <button className={Styles.btn}>+</button>
                      <h2 className={Styles.quantity}>{item.quantity}</h2>
                      <button
                        onClick={() => removeFromCart(item.id)}
                        className={Styles.btn}
                      >
                        -
                      </button>
                    </div>
                    <ul>
                      <div className={Styles['arrow-container']}>
                        <img src={LeftArrow} className={Styles.arrow} />
                        <img src={RightArrow} className={Styles.arrow} />
                      </div>
                      {item.gallery.map((gallery) => {
                        return (
                          <li key={gallery}>
                            <img src={gallery} className={Styles.img} />
                          </li>
                        )
                      })}
                    </ul>
                  </div>
                </li>
              )
            })}
          </ul>
        </div>
      </div>
    )
  }
}

Cart.contextType = Context

export default Cart
