import React from 'react'
import Styles from './cartComponent.module.css'
import Context from '../../Context'
import { client } from '../../index'
import { gql } from '@apollo/client'

class CartComponent extends React.Component {
  constructor() {
    super()
    this.state = {
      data: undefined,
    }
  }
  componentDidMount() {
    client
      .query({
        query: gql`
          query {
            product(id: "${this.props.item}") {
              id
                name
              brand
              inStock
              gallery
               __typename
              attributes{
                id
                items {
                  value
                }
              }
              prices {
                amount
                  currency{
                    symbol
                    label
                  }
                }
              description
            }
          }
        `,
      })
      .then((result) =>
        this.setState({
          data: result.data,
        })
      )
  }

  test = () => {
    if (this.state.data) {
      console.log(this.state.data.product.brand)
    } else {
      console.log('error')
    }
  }
  render() {
    const { removeFromCart } = this.context
    const { addToCart } = this.context
    const { index } = this.context
    return (
      <li className={Styles['description-container']}>
        {this.state.data && (
          <div>
            <div>
              <h2 className={Styles.brand}>{this.state.data.product.brand}</h2>
              <h2 className={Styles.name}>{this.state.data.product.name}</h2>
              <h2 className={Styles.price}>
                {this.state.data.product.prices[index].currency.symbol}
                {this.props.itemAmount}
              </h2>
              {Object.entries(this.props.itemAttributes).map(([key, value]) => {
                return (
                  <div key={key}>
                    <h2>{key}</h2>
                    <h2>{value}</h2>
                  </div>
                )
              })}
            </div>
            <div className={Styles['right-side']}>
              <div className={Styles['quantity-container']}>
                <button
                  className={Styles.btn}
                  onClick={() => addToCart(this.props.addToCartItem)}
                >
                  +
                </button>
                <h2 className={Styles.quantity}>{this.props.itemQuantity}</h2>
                <button
                  onClick={() => removeFromCart(this.props.removeFromCartItem)}
                  className={Styles.btn}
                >
                  -
                </button>

                <button onClick={this.test}>test</button>
              </div>
              <img src={this.props.itemImg} className={Styles.img} />
            </div>
          </div>
        )}
      </li>
    )
  }
}

CartComponent.contextType = Context

export default CartComponent
