import { render } from '@testing-library/react'
import Nav from '../nav/nav'
import React from 'react'
import Context from '../Context'

class Cart extends React.Component {
  render() {
    const { cartList } = this.context
    return (
      <div>
        <Nav />
        <div>
          <div>
            <h2>Cart</h2>
          </div>
          <ul>
            {cartList.map((item) => {
              return (
                <li key={item.id}>
                  <div>
                    <h2>{item.brand}</h2>
                    <h2>{item.name}</h2>
                    <h2>
                      {item.currency}
                      {item.amount}
                    </h2>
                    <ul>
                      {item.attributes.map((attributes) => {
                        return (
                          <li>
                            <div>{attributes}</div>
                          </li>
                        )
                      })}
                    </ul>
                  </div>
                  <div>
                    <h2>{item.quantity}</h2>
                    <ul>
                      {item.gallery.map((gallery) => {
                        return (
                          <li key={gallery}>
                            <img src={gallery} />
                          </li>
                        )
                      })}
                    </ul>
                  </div>
                </li>
              )
            })}
          </ul>
        </div>
      </div>
    )
  }
}

Cart.contextType = Context

export default Cart
