import Nav from '../nav/nav'
import React from 'react'
import Styles from './cart.module.css'
import Context from '../Context'
import { client } from '../index'
import { gql } from '@apollo/client'

class Cart extends React.Component {
  constructor() {
    super()
    this.state = {
      index: 0,
      data: undefined,
    }
  }
  componentDidMount() {
    const { cartList } = this.context
    client
      .query({
        query: gql`
          query {
            product(id: "${cartList.id}") {
              id
                name
              brand
              inStock
              gallery
               __typename
              attributes{
                id
                items {
                  value
                }
              }
              prices {
                amount
                  currency{
                    symbol
                    label
                  }
                }
              description
            }
          }
        `,
      })
      .then((result) =>
        this.setState({
          data: result.data,
        })
      )
  }

  render() {
    const { cartList } = this.context
    const { removeFromCart } = this.context
    const { addToCart } = this.context
    const { index } = this.context

    return (
      <div className={Styles['main-container']}>
        <div>
          <h2 className={Styles['cart-text']}>Cart</h2>
        </div>
        <ul>
          {cartList.map((item) => {
            return (
              <li key={item.id} className={Styles['description-container']}>
                <div>
                  <h2 className={Styles.brand}>{item.brand}</h2>
                  <h2 className={Styles.name}>{item.name}</h2>
                  <h2 className={Styles.price}>
                    {item.prices[index].currency.symbol}
                    {item.prices[index].amount}
                  </h2>
                  {Object.entries(item.attributes).map(([key, value]) => {
                    return (
                      <div key={key}>
                        <h2>{key}</h2>
                        <h2>{value}</h2>
                      </div>
                    )
                  })}
                </div>
                <div className={Styles['right-side']}>
                  <div className={Styles['quantity-container']}>
                    <button
                      className={Styles.btn}
                      onClick={() => addToCart(item)}
                    >
                      +
                    </button>
                    <h2 className={Styles.quantity}>{item.quantity}</h2>
                    <button
                      onClick={() => removeFromCart(item.id)}
                      className={Styles.btn}
                    >
                      -
                    </button>
                  </div>
                  <img src={item.gallery[0]} className={Styles.img} />
                </div>
              </li>
            )
          })}
        </ul>
      </div>
    )
  }
}

Cart.contextType = Context

export default Cart
