import React from 'react'
import Styles from './cart.module.css'
import Context from '../Context'
import CartComponent from './cartComponents/CartComponent'
import { Link } from 'react-router-dom'

class Cart extends React.Component {
  constructor() {
    super()
    this.state = {
      index: 0,
      data: undefined,
    }
  }

  render() {
    const { cartList, orderItem } = this.context
    return (
      <div className={Styles['main-container']}>
        <div>
          <h2 className={Styles['cart-text']}>Cart</h2>
        </div>
        <ul>
          {cartList.map((item) => {
            return (
              <CartComponent
                key={item.id}
                item={item.id}
                itemQuantity={item.quantity}
                addToCartItem={item}
                removeFromCartItem={item.id}
              />
            )
          })}
        </ul>
        {cartList.length === 2 && (
          <div className={Styles.summary}>
            <div>
              <h2 className={Styles.text}>Tax21%: </h2>
              <h2 className={Styles.text}>Quantity:</h2>
              <h2 className={Styles.text}>Total: </h2>
            </div>
            <Link to='/'>
              <button className={Styles.btn} onClick={orderItem}>
                order
              </button>
            </Link>
          </div>
        )}
      </div>
    )
  }
}

Cart.contextType = Context

export default Cart
